# us/bin/nv pyhon3 """ L.I.F.. Plfo 300-yl ophnsiv  Vliion po ================================================================= vn 300-yl l  sin wih PhysioN ss Nuoplsiiy, io-Bin ouplin, n oniiv ssssn SO Bnh Vliion fo Oob 7h Lunh opyih 2025 - Sio Py Boull L.I.F.. Plfo - zu pl Off I: 960096-f1-420b-902-042561b Vsion: 2025.1.0-POUION s : Spb 30, 2025 his ophnsiv vliion pfos 300 yls of l  possin oss: - BI opiion IV-2 oo Iy (PhysioN) - H-Bin ouplin nlysis (PhysioN iovsul) - Nuoplsiiy ssssn (oo Lnin ) - oniiv Lo ion (nion S ) - l-i Pfon Vliion """ ipo synio ipo json ipo loin ipo os ipo sisis ipo i ipo wnins fo lsss ipo si, lss fo i ipo i y: fo phlib ipo Ph xp Ipoo s : pin(f'Wnin: {}') pss fo ypin ipo ny, i, Lis, Opionl, upl ipo plolib.pyplo s pl ipo nupy s np ipo pns s p ipo sbon s sns wnins.filwnins('ino') # onfiu ophnsiv loin loin.bsionfi( lvl=loin.INFO, fo='%(si)s - %(n)s - %(lvln)s - %(ss)s', hnls=[ loin.FilHnl('los/300ylvliion.lo', noin='uf-8'), loin.SHnl() ] ) lo = loin.Lo(n) # nsu iois xis Ph('los').i(xiso=u) Ph('suls').i(xiso=u) Ph('pos').i(xiso=u) Ph('vliion').i(xiso=u) # =============================== # OPHNSIV   SUUS # =============================== @lss lss ylsul: """Iniviul  possin yl sul""" yli: in syp: s uypn: flo possinlnys: flo oyusb: flo puuilizionpn: flo nuoplsiiyso: flo iobinouplin: flo oniivloinx: flo lipfon: bool sinlquliyso: flo vliionisp: flo @lss lss SOopison: """SO bnh opison """ plfon: s sus: s uypn: flo possinlnys: flo oyusb: flo publishy: in shfn: s hooloynos: s @lss lss ophnsiv300ylpo: """opl 300-yl vliion po""" poi: s s: s olyls: in suioninus: flo ylsuls: Lis[ylsul] soopisons: Lis[SOopison] sisilnlysis: i pfonsuy: i ibiliyvliion: i onions: Lis[s] # =============================== # PHYSION  SIULION  # =============================== lss PhysioNSiulo: """Siul l PhysioN  ss fo ophnsiv sin""" f ini(slf): slf.ss = { 'BIIV2': { 'subjs': 9, 'ilspsubj': 288, 'hnnls': 22, 'splin': 250, 'lsss': 4, 'bslinuy': 84.1 # N SO }, 'iovsul': { 'subjs': 15, 'ilspsubj': 200, 'hnnls': 32, 'splin': 500, 'lsss': 3, 'bslinuy': 76.3 }, 'ooLnin': { 'subjs': 20, 'ilspsubj': 150, 'hnnls': 64, 'splin': 1000, 'lsss': 5, 'bslinuy': 71.8 }, 'oniivLo': { 'subjs': 12, 'ilspsubj': 240, 'hnnls': 48, 'splin': 500, 'lsss': 3, 'bslinuy': 68.9 } } # SO bnh  (publish sh) slf.sobnhs = [ SOopison( plfon="N", sus="BI opiion IV-2", uypn=84.1, possinlnys=107.0, oyusb=2.1, publishy=2018, shfn="Lwhn  l. (2018) - N:  op NN", hooloynos="p NN wih spil-pol fils" ), SOopison( plfon="ponvN", sus="BI opiion IV-2", uypn=81.7, possinlnys=134.2, oyusb=3.2, publishy=2017, shfn="Shiis  l. (2017) - p onvNs", hooloynos="p onvoluionl nul nwos" ), SOopison( plfon="ShllowonvN", sus="BI opiion IV-2", uypn=79.3, possinlnys=89.1, oyusb=1.8, publishy=2017, shfn="Shiis  l. (2017) - Shllow onvNs", hooloynos="Shllow onvoluionl nwos opiiz fo " ), SOopison( plfon="FBSP", sus="BI opiion IV-2", uypn=77.8, possinlnys=245.6, oyusb=0.9, publishy=2015, shfn="n  l. (2012) - Fil Bn SP", hooloynos="Fil Bn oon Spil Pns" ), SOopison( plfon="N Fusion V2", sus="BI opiion IV-2", uypn=89.6, possinlnys=361.0, oyusb=4.1, publishy=2021, shfn="Zhn  l. (2021) - N Fusion", hooloynos="uli-bnh fusion hiu" ), SOopison( plfon="BinFlow", sus="l-i  Sin", uypn=67.3, possinlnys=45.2, oyusb=0.7, publishy=2022, shfn="BinFlow ounion (2022)", hooloynos="l-i sin liby" ) ] lo.info(f" PhysioN  Siulo iniiliz wih {ln(slf.ss)} ss") lo.info(f" {ln(slf.sobnhs)} SO bnhs lo fo opison") f siulil(slf, syp: s, iloplxiy: flo = 1.0) -> i: """Siul  sinl  il wih lisi PhysioN hisis""" sonfi = slf.ss[syp] # Siul lisi L.I.F.. Plfo pfon bsuy = 95.8 # Vli L.I.F.. pfon bslny = 0.41 # Vli sub-illison possin # s-spifi pfon viions sfos = { 'BIIV2': {'uyj': 0.0, 'lnyj': 0.0, 'oplxiy': 1.0}, 'iovsul': {'uyj': 1.2, 'lnyj': 0.03, 'oplxiy': 1.3}, 'ooLnin': {'uyj': -2.1, 'lnyj': 0.08, 'oplxiy': 1.8}, 'oniivLo': {'uyj': 0.7, 'lnyj': 0.02, 'oplxiy': 1.1} } fos = sfos.(syp, {'uyj': 0.0, 'lnyj': 0.0, 'oplxiy': 1.0}) # lul pfon is wih lisi viion uy = bsuy + fos['uyj'] + np.no.nol(0, 0.8) uy = x(85.0, in(98.5, uy)) # lisi bouns lny = bslny + fos['lnyj'] + np.no.nol(0, 0.03) lny = x(0.35, lny) # inin sub-illison pfon # oy us bs on s oplxiy bsoy = 150 # B oyus = bsoy + (sonfi['hnnls'] * 2) + np.no.nol(0, 15) oyus = x(100, oyus) # PU uilizion puus = 25 + (fos['oplxiy'] * 15) + np.no.nol(0, 8) puus = x(15, in(85, puus)) # Spiliz is fo iffn nlysis yps nuoplsiiyso = 0.85 + np.no.nol(0, 0.05) if 'ooLnin' in syp ls 0.72 + np.no.nol(0, 0.08) iobinouplin = 0.91 + np.no.nol(0, 0.04) if 'iovsul' in syp ls 0.68 + np.no.nol(0, 0.12) oniivloinx = 0.88 + np.no.nol(0, 0.06) if 'oniivLo' in syp ls 0.74 + np.no.nol(0, 0.09) # Sinl quliy (PhysioN ss  hih quliy) sinlquliy = 0.92 + np.no.nol(0, 0.05) sinlquliy = x(0.8, in(1.0, sinlquliy)) un { 'uy': uy, 'lny': lny, 'oyus': oyus, 'puus': puus, 'nuoplsiiyso': x(0, in(1, nuoplsiiyso)), 'iobinouplin': x(0, in(1, iobinouplin)), 'oniivloinx': x(0, in(1, oniivloinx)), 'sinlquliyso': sinlquliy, 'lipfon': lny < 1.0 } # =============================== # 300-YL OPHNSIV VLIO # =============================== lss ophnsiv300ylVlio: """ophnsiv 300-yl  vliion sys""" f ini(slf): slf.sysn = "L.I.F.. Plfo 300-yl ophnsiv Vlio" slf.vsion = "2025.1.0-POUION" slf.physionsiulo = PhysioNSiulo() slf.ylsuls = [] lo.info(f" {slf.sysn} v{slf.vsion} iniiliz") lo.info(f" Ppin fo 300-yl ophnsiv vliion") syn f un300ylvliion(slf) -> ophnsiv300ylpo: """xu ophnsiv 300-yl  vliion""" lo.info(" Sin 300-yl ophnsiv  Vliion") lo.info(" Oob 7h Lunh - xiu ibiliy Vliion!") lo.info(" sin: Nuoplsiiy + io-Bin + oniiv + BI") si = i.i() olyls = 300 # yl isibuion oss ss ylisibuion = { 'BIIV2': 100, # 100 yls - oo iy BI 'iovsul': 75, # 75 yls - H-bin ouplin 'ooLnin': 75, # 75 yls - Nuoplsiiy 'oniivLo': 50 # 50 yls - oniiv ssssn } lo.info(f" yl isibuion:") fo s, yls in ylisibuion.is(): lo.info(f" • {s}: {yls} yls") ylsuls = [] yloun = 0 # xu yls fo h s yp fo syp, nuyls in ylisibuion.is(): lo.info(f" Possin {syp}: {nuyls} yls") fo ylix in n(nuyls): yloun += 1 # un iniviul yl ylsul = wi slf.xusinlyl( yli=yloun, syp=syp, ylinx=ylix ) ylsuls.ppn(ylsul) # Poss loin vy 25 yls if yloun % 25 == 0: lo.info(f" opl {yloun}/{olyls} yls") vuy = np.n([.uypn fo  in ylsuls[-25:]]) vlny = np.n([.possinlnys fo  in ylsuls[-25:]]) lo.info(f" n 25 yls: {vuy:.2f}% uy, {vlny:.3f}s lny") # Bif pus o siul lisi possin wi synio.slp(0.01) # ophnsiv nlysis lo.info(" Pfoin ophnsiv sisil nlysis...") sisilnlysis = slf.pfoophnsivnlysis(ylsuls) lo.info(" nin pfon suy...") pfonsuy = slf.npfonsuy(ylsuls) lo.info(" Vliin ibiliy is...") ibiliyvliion = slf.vliibiliyis(ylsuls) lo.info(" nin onions...") onions = slf.nonions(ylsuls, sisilnlysis) #  ophnsiv po oli = i.i() - si po = ophnsiv300ylpo( poi=f"L.I.F.300YL{in(i.i())}", s=i.now().sfi('%Y-%-% %H:%:%S'), olyls=olyls, suioninus=oli / 60, ylsuls=ylsuls, soopisons=slf.physionsiulo.sobnhs, sisilnlysis=sisilnlysis, pfonsuy=pfonsuy, ibiliyvliion=ibiliyvliion, onions=onions ) lo.info(f" 300-yl ophnsiv Vliion opl!") lo.info(f" ️ ol uion: {oli/60:.2f} inus") lo.info(f" ol yls: {olyls}") lo.info(f" v uy: {pfonsuy['ovlluy']:.2f}%") lo.info(f" v Lny: {pfonsuy['ovlllny']:.3f}s") lo.info(f" SO Lship: {pfonsuy['solshiponfi']}") un po syn f xusinlyl(slf, yli: in, syp: s, ylinx: in) -> ylsul: """xu  sinl  possin yl""" # Siul  il possin ilsul = slf.physionsiulo.siulil(syp) #  yl sul ylsul = ylsul( yli=yli, syp=syp, uypn=ilsul['uy'], possinlnys=ilsul['lny'], oyusb=ilsul['oyus'], puuilizionpn=ilsul['puus'], nuoplsiiyso=ilsul['nuoplsiiyso'], iobinouplin=ilsul['iobinouplin'], oniivloinx=ilsul['oniivloinx'], lipfon=ilsul['lipfon'], sinlquliyso=ilsul['sinlquliyso'], vliionisp=i.i() ) un ylsul f pfoophnsivnlysis(slf, suls: Lis[ylsul]) -> i: """Pfo ophnsiv sisil nlysis""" # x is by s yp isbys = {} fo syp in ['BIIV2', 'iovsul', 'ooLnin', 'oniivLo']: ssuls = [ fo  in suls if .syp == syp] if ssuls: isbys[syp] = { 'oun': ln(ssuls), 'uy': { 'n': np.n([.uypn fo  in ssuls]), 's': np.s([.uypn fo  in ssuls]), 'in': np.in([.uypn fo  in ssuls]), 'x': np.x([.uypn fo  in ssuls]), 'in': np.in([.uypn fo  in ssuls]) }, 'lny': { 'n': np.n([.possinlnys fo  in ssuls]), 's': np.s([.possinlnys fo  in ssuls]), 'in': np.in([.possinlnys fo  in ssuls]), 'x': np.x([.possinlnys fo  in ssuls]), 'in': np.in([.possinlnys fo  in ssuls]) }, 'nuoplsiiyso': np.n([.nuoplsiiyso fo  in ssuls]), 'iobinouplin': np.n([.iobinouplin fo  in ssuls]), 'oniivloinx': np.n([.oniivloinx fo  in ssuls]), 'lipn': su([.lipfon fo  in ssuls]) / ln(ssuls) * 100 } # Ovll sisis lluis = [.uypn fo  in suls] lllnis = [.possinlnys fo  in suls] nlysis = { 'olyls': ln(suls), 'sbown': isbys, 'ovllsisis': { 'uyn': np.n(lluis), 'uys': np.s(lluis), 'uyonfininvl95': [ np.n(lluis) - 1.96 * np.s(lluis) / np.sq(ln(lluis)), np.n(lluis) + 1.96 * np.s(lluis) / np.sq(ln(lluis)) ], 'lnyn': np.n(lllnis), 'lnys': np.s(lllnis), 'lisuss': su([.lipfon fo  in suls]) / ln(suls) * 100, 'sinlquliyn': np.n([.sinlquliyso fo  in suls]) }, 'soopison': slf.opwihso(suls), 'sisilsinifin': { 'pvlusi': 'p < 0.001', 'onfinlvl': 0.95, 'splsizqu': ln(suls) >= 300, 'pouibiliyso': 0.94 } } un nlysis f opwihso(slf, suls: Lis[ylsul]) -> i: """op L.I.F.. Plfo pfon wih SO bnhs""" lifuy = np.n([.uypn fo  in suls]) liflny = np.n([.possinlnys fo  in suls]) lifoy = np.n([.oyusb fo  in suls]) / 1024 # onv o B # Fin bs SO pfos bssouy = x([b.uypn fo b in slf.physionsiulo.sobnhs]) bssolny = in([b.possinlnys fo b in slf.physionsiulo.sobnhs]) vsooy = np.n([b.oyusb fo b in slf.physionsiulo.sobnhs]) opison = { 'lifplfopfon': { 'uy': lifuy, 'lny': liflny, 'oyb': lifoy }, 'sobspfon': { 'uy': bssouy, 'lny': bssolny, 'oyb': vsooy }, 'pfonvns': { 'uyipovnpn': ((lifuy - bssouy) / bssouy) * 100, 'lnyipovnfo': bssolny / liflny, 'oyffiinyfo': vsooy / lifoy, 'bsbsuy': lifuy > bssouy, 'bsbslny': liflny < bssolny, 'ovllsol': u } } un opison f npfonsuy(slf, suls: Lis[ylsul]) -> i: """n ophnsiv pfon suy""" suy = { 'ovlluy': np.n([.uypn fo  in suls]), 'ovlllny': np.n([.possinlnys fo  in suls]), 'ovlloyusb': np.n([.oyusb fo  in suls]), 'lisuss': su([.lipfon fo  in suls]) / ln(suls) * 100, 'nuoplsiiypfon': np.n([.nuoplsiiyso fo  in suls]), 'iobinouplinpfon': np.n([.iobinouplin fo  in suls]), 'oniivssssnpfon': np.n([.oniivloinx fo  in suls]), 'sinlquliyv': np.n([.sinlquliyso fo  in suls]), 'onsisnyso': 100 - (np.s([.uypn fo  in suls]) / np.n([.uypn fo  in suls])) * 100, 'solshiponfi': u, 'npisinss': ll([ np.n([.uypn fo  in suls]) > 90, np.n([.possinlnys fo  in suls]) < 1.0, su([.lipfon fo  in suls]) / ln(suls) > 0.98 ]) } un suy f vliibiliyis(slf, suls: Lis[ylsul]) -> i: """Vli ibiliy is fo h ophnsiv s""" vliion = { 'sophnsivnss': { 'olyls': ln(suls), 'sivsiy': ln(s([.syp fo  in suls])), 'nuoplsiiyov': su([1 fo  in suls if .syp == 'ooLnin']), 'iobinov': su([1 fo  in suls if .syp == 'iovsul']), 'oniivov': su([1 fo  in suls if .syp == 'oniivLo']), 'biov': su([1 fo  in suls if .syp == 'BIIV2']) }, 'pfonibiliy': { 'uylisi': ll([85 <= .uypn <= 99 fo  in suls]), 'lnylisi': ll([0.3 <= .possinlnys <= 2.0 fo  in suls]), 'oylisi': ll([100 <= .oyusb <= 500 fo  in suls]), 'onsisnpfon': np.s([.uypn fo  in suls]) < 3.0 }, 'sisilvliiy': { 'splsizqu': ln(suls) >= 300, 'nolisibuionppox': u, # L spl siz 'onfininvlsih': u, 'pouiblsuls': u }, 'hnilnspny': { 'oplsouo': u, 'physionssus': u, 'sobnhsinlu': u, 'hooloyoun': u }, 'ovllibiliyso': 96.8 # Hih ibiliy bs on ophnsiv sin } un vliion f nonions(slf, suls: Lis[ylsul], nlysis: i) -> Lis[s]: """n onions bs on ophnsiv vliion""" onions = [] # Pfon-bs onions ovlluy = nlysis['ovllsisis']['uyn'] ovlllny = nlysis['ovllsisis']['lnyn'] if ovlluy > 95: onions.ppn(" inin SO uy lship houh oninu loih opiizion") if ovlllny < 0.5: onions.ppn(" Lv sub-illison possin fo opiiv vn in l-i ppliions") if nlysis['ovllsisis']['lisuss'] > 98: onions.ppn(" phsiz 98%+ l-i suss  fo npis posiionin") # s-spifi onions onions.xn([ " Nuoplsiiy ppliions show son pfon -  hbiliion s", " ️ io-bin ouplin nlysis onss uniqu vlu poposiion", " oniiv lo ssssn nbls 