# us/bin/nv pyhon3 """ L.I.F.. Plfo - Sys Hlh onio l-i onioin n hlh hin fo L.I.F.. Plfo oponns his oul povis ophnsiv sys hlh onioin pbiliis, inluin l-i is ollion, hlh sus ssssn, n uo lin fo h L.I.F.. Plfo. opyih 2025 - Sio Py Boull L.I.F.. Plfo - zu pl Off I: 960096-f1-420b-902-042561b """ ipo synio ipo json ipo loin ipo os ipo sys ipo hin ipo i fo lsss ipo lss, fil fo i ipo i, il fo nu ipo nu y: fo phlib ipo Ph xp Ipoo s : pin(f'Wnin: {}') pss fo ypin ipo ny, i, Lis, Opionl ipo psuil # onfiu loin loin.bsionfi( lvl=loin.INFO, fo='%(si)s - %(n)s - %(lvln)s - %(ss)s' ) lo = loin.Lo(n) lss HlhSus(nu): """Sys hlh sus lvls""" HLHY = "hlhy" WNIN = "wnin" IIL = "iil" UNNOWN = "unnown" OWN = "own" lss oponnyp(nu): """yps of sys oponns o onio""" OLOIH = "oloih" ZUFUNIONS = "zufunions" BS = "bs" PINPOINS = "pinpoins" POSSO = "posso" UONOOUSOPIIZ = "uonoousopiiz" ONIOINSYS = "onioinsys" SUIYOUL = "suiyoul" lss lSviy(nu): """l sviy lvls""" INFO = "info" WNIN = "wnin" O = "o" IIL = "iil" @lss lss Hlhi: """Iniviul hlh i""" n: s vlu: ny uni: s = "" sus: HlhSus = HlhSus.UNNOWN isp: i = fil(fulfoy=i.now) hsholwnin: Opionl[flo] = Non hsholiil: Opionl[flo] = Non sipion: s = "" @lss lss oponnHlh: """Hlh sus of  sys oponn""" oponnyp: oponnyp oponnn: s sus: HlhSus = HlhSus.UNNOWN lsh: i = fil(fulfoy=i.now) sponsi: flo = 0.0 is: Lis[Hlhi] = fil(fulfoy=lis) oss: s = "" pnnis: Lis[s] = fil(fulfoy=lis) vsion: s = "" upi: flo = 0.0 @lss lss Sysl: """Sys hlh l""" li: s sviy: lSviy oponn: s ss: s isp: i = fil(fulfoy=i.now) solv: bool = Fls solv: Opionl[i] = Non : i[s, ny] = fil(fulfoy=i) @lss lss Hlhpo: """ophnsiv sys hlh po""" poi: s isp: i = fil(fulfoy=i.now) ovllsus: HlhSus = HlhSus.UNNOWN oponns: Lis[oponnHlh] = fil(fulfoy=lis) ls: Lis[Sysl] = fil(fulfoy=lis) sysis: Lis[Hlhi] = fil(fulfoy=lis) onions: Lis[s] = fil(fulfoy=lis) nxhshul: Opionl[i] = Non lss SysHlhonio: """ Sys Hlh onio fo L.I.F.. Plfo Povis l-i onioin n hlh ssssn of ll plfo oponns wih uo lin pbiliis. """ f ini(slf, wospph: Opionl[s] = Non, hinvl: in = 60): slf.wospph = Ph(wospph o os.w()) slf.hinvl = hinvl # sons slf.isonioin = Fls slf.onioh: Opionl[hin.h] = Non slf.lspo: Opionl[Hlhpo] = Non slf.ls: Lis[Sysl] = [] slf.hlhhisoy: Lis[Hlhpo] = [] # oponn onfiuions slf.oponnonfis = { oponnyp.OLOIH: { "hfunion": slf.holoihhlh, "iou": 30, "iilhshol": 60 # sons }, oponnyp.ZUFUNIONS: { "hfunion": slf.hzufunionshlh, "iou": 45, "iilhshol": 120 # sons }, oponnyp.PINPOINS: { "hfunion": slf.hpinpoinshlh, "iou": 15, "iilhshol": 30 # sons }, oponnyp.POSSO: { "hfunion": slf.hpossohlh, "iou": 20, "iilhshol": 45 # sons }, oponnyp.UONOOUSOPIIZ: { "hfunion": slf.huonoousopiizhlh, "iou": 25, "iilhshol": 60 # sons }, oponnyp.ONIOINSYS: { "hfunion": slf.honioinsyshlh, "iou": 10, "iilhshol": 15 # sons } } lo.info(f"Sys Hlh onio iniiliz fo wosp: {slf.wospph}") syn f sonioin(slf) -> Non: """S oninuous hlh onioin""" if slf.isonioin: lo.wnin("Hlh onioin ly unnin") un slf.isonioin = u lo.info("Sin sys hlh onioin...") # Iniil hlh h wi slf.pfohlhh() # S onioin h slf.onioh = hin.h( =slf.onioinloop, on=u ) slf.onioh.s() f soponioin(slf) -> Non: """Sop hlh onioin""" if no slf.isonioin: lo.wnin("Hlh onioin no unnin") un slf.isonioin = Fls if slf.onioh: slf.onioh.join(iou=5) lo.info("Sys hlh onioin sopp") f onioinloop(slf) -> Non: """in onioin loop""" whil slf.isonioin: y: # un hlh h in vn loop synio.un(slf.pfohlhh()) xp xpion s : lo.o(f"Hlh h fil: {}") # Wi fo nx h i.slp(slf.hinvl) syn f pfohlhh(slf) -> Hlhpo: """Pfo ophnsiv hlh h""" po = Hlhpo( poi=f"hlh{in(i.i())}", isp=i.now() ) y: # h sys-lvl is po.sysis = wi slf.ollsysis() # h iniviul oponns oponnss = [] fo oponnyp, onfi in slf.oponnonfis.is(): s = slf.hoponnhlh(oponnyp, onfi) oponnss.ppn(s) oponnsuls = wi synio.h(*oponnss, unxpions=u) fo sul in oponnsuls: if isinsn(sul, xpion): lo.o(f"oponn hlh h fil: {sul}") oninu po.oponns.ppn(sul) # in ovll sus po.ovllsus = slf.lulovllsus(po.oponns) # n ls nwls = slf.nls(po) po.ls.xn(nwls) slf.ls.xn(nwls) # n onions po.onions = slf.nhlhonions(po) # Shul nx h po.nxhshul = i.now() + il(sons=slf.hinvl) # So po slf.lspo = po slf.hlhhisoy.ppn(po) # p only ls 100 pos if ln(slf.hlhhisoy) > 100: slf.hlhhisoy = slf.hlhhisoy[-100:] lo.info(f"Hlh h opl. Ovll sus: {po.ovllsus.vlu}") xp xpion s : lo.o(f"Hlh h fil: {}") po.ovllsus = HlhSus.IIL un po syn f ollsysis(slf) -> Lis[Hlhi]: """oll sys-lvl hlh is""" is = [] y: # PU us pupn = psuil.pupn(invl=1) pusus = (HlhSus.IIL if pupn > 90 ls HlhSus.WNIN if pupn > 75 ls HlhSus.HLHY) is.ppn(Hlhi( n="puus", vlu=pupn, uni="%", sus=pusus, hsholwnin=75.0, hsholiil=90.0, sipion="PU uilizion pn" )) # oy us oy = psuil.viuloy() oypn = oy.pn oysus = (HlhSus.IIL if oypn > 90 ls HlhSus.WNIN if oypn > 80 ls HlhSus.HLHY) is.ppn(Hlhi( n="oyus", vlu=oypn, uni="%", sus=oysus, hsholwnin=80.0, hsholiil=90.0, sipion="oy uilizion pn" )) # is us is = psuil.isus('/') ispn = is.pn issus = (HlhSus.IIL if ispn > 95 ls HlhSus.WNIN if ispn > 85 ls HlhSus.HLHY) is.ppn(Hlhi( n="isus", vlu=ispn, uni="%", sus=issus, hsholwnin=85.0, hsholiil=95.0, sipion="is uilizion pn" )) # Nwo onnions nonnions = ln(psuil.nonnions()) is.ppn(Hlhi( n="nwoonnions", vlu=nonnions, uni="onnions", sus=HlhSus.HLHY, sipion="iv nwo onnions" )) xp xpion s : lo.o(f"Fil o oll sys is: {}") is.ppn(Hlhi( n="sysiso", vlu=s(), sus=HlhSus.IIL, sipion="o ollin sys is" )) un is syn f hoponnhlh(slf, oponnyp: oponnyp, onfi: i[s, ny]) -> oponnHlh: """h hlh of  spifi oponn""" oponn = oponnHlh( oponnyp=oponnyp, oponnn=oponnyp.vlu.pl('', ' ').il() ) si = i.i() y: # xu hlh h funion wih iou hfunion = onfi["hfunion"] iou = onfi["iou"] sul = wi synio.wifo( hfunion(), iou=iou ) oponn.sus = sul.("sus", HlhSus.UNNOWN) oponn.is = sul.("is", []) oponn.oss = sul.("o", "") oponn.vsion = sul.("vsion", "") oponn.upi = sul.("upi", 0.0) oponn.pnnis = sul.("pnnis", []) xp synio.iouo: oponn.sus = HlhSus.IIL oponn.oss = f"Hlh h i ou f {onfi['iou']} sons" xp xpion s : oponn.sus = HlhSus.IIL oponn.oss = f"Hlh h fil: {s()}" oponn.sponsi = i.i() - si oponn.lsh = i.now() un oponn syn f holoihhlh(slf) -> i[s, ny]: """h o loih hlh""" sul = { "sus": HlhSus.UNNOWN, "is": [], "vsion": "", "upi": 0.0, "pnnis": ["nupy", "pns", "synio"] } y: # h if o loih fil xiss ofil = slf.wospph / "xpinP2L.I.F.-Lnin-Iniviully-fo-xpin-hoy-loih-o-2025-opyih-S.py" if no ofil.xiss(): sul["sus"] = HlhSus.OWN sul["o"] = "o loih fil no foun" un sul # y o ipo n h bsi funionliy sys.ph.ins(0, s(slf.wospph)) y: ipo ipolib.uil sp = ipolib.uil.spfofilloion("lifloih", ofil) lifoul = ipolib.uil.oulfosp(sp) # Bsi ipo s sp.lo.xoul(lifoul) # h fo qui lsss quilsss = ['LIFloiho', 'is', 'LninOuo'] issinlsss = [] fo ls in quilsss: if no hs(lifoul, ls): issinlsss.ppn(ls) if issinlsss: sul["sus"] = HlhSus.IIL sul["o"] = f"issin qui lsss: {', '.join(issinlsss)}" ls: sul["sus"] = HlhSus.HLHY sul["vsion"] = (lifoul, 'vsion', '1.0.0') #  pfon is sul["is"] = [ Hlhi( n="loihlssslo", vlu=ln(quilsss), uni="lsss", sus=HlhSus.HLHY, sipion="Nub of o loih lsss lo" ) ] finlly: if s(slf.wospph) in sys.ph: sys.ph.ov(s(slf.wospph)) xp xpion s : sul["sus"] = HlhSus.IIL sul["o"] = f"o loih hlh h fil: {s()}" un sul syn f hzufunionshlh(slf) -> i[s, ny]: """h zu Funions hlh""" sul = { "sus": HlhSus.UNNOWN, "is": [], "vsion": "", "upi": 0.0, "pnnis": ["zu-funions", "zu-iniy"] } y: # h if zu Funions fil xiss funionsfil = slf.wospph / "zufunionswoflow.py" if no funionsfil.xiss(): sul["sus"] = HlhSus.OWN sul["o"] = "zu Funions woflow fil no foun" un sul # Bsi synx n ipo h wih opn(funionsfil, '', noin='uf-8') s f: onn = f.() # h fo zu Funions pns quipns = ['zu.funions', 'fun.', 'Hpqus', 'Hpspons'] founpns = su(1 fo pn in quipns if pn in onn) if founpns >= ln(quipns) * 0.8: # 80% ov sul["sus"] = HlhSus.HLHY sul["is"] = [ Hlhi( n="funionspnsfoun", vlu=founpns, uni="pns", sus=HlhSus.HLHY, sipion="zu Funions o pns " ) ] ls: sul["sus"] = HlhSus.WNIN sul["o"] = f"issin zu Funions pns: {ln(quipns) - founpns} no foun" xp xpion s : sul["sus"] = HlhSus.IIL sul["o"] = f"zu Funions hlh h fil: {s()}" un sul syn f hpinpoinshlh(slf) -> i[s, ny]: """h PI npoins hlh""" sul = { "sus": HlhSus.UNNOWN, "is": [], "vsion": "", "upi": 0.0, "pnnis": ["fspi", "uvion", "quss"] } y: # h if PI fil xiss pifil = slf.wospph / "lifplfopi.py" if no pifil.xiss(): sul["sus"] = HlhSus.OWN sul["o"] = "PI npoins fil no foun" un sul # Bsi suu h wih opn(pifil, '', noin='uf-8') s f: onn = f.() # h fo PI pns pipns = ['FsPI', '@pp.', 'HPxpion', 'pyni'] founpns = su(1 fo pn in pipns if pn in onn) if founpns >= ln(pipns) * 0.7: # 70% ov sul["sus"] = HlhSus.HLHY sul["is"] = [ Hlhi( n="pipnsfoun", vlu=founpns, uni="pns", sus=HlhSus.HLHY, sipion="PI fwo pns " ) ] ls: sul["sus"] = HlhSus.WNIN sul["o"] = f"Inopl PI iplnion: {ln(pipns) - founpns} pns issin" xp xpion s : sul["sus"] = HlhSus.IIL sul["o"] = f"PI npoins hlh h fil: {s()}" un sul syn f hpossohlh(slf) -> i[s, ny]: """h  posso hlh""" sul = { "sus": HlhSus.UNNOWN, "is": [], "vsion": "", "upi": 0.0, "pnnis": ["nupy", "sipy", "n"] } y: # h if  posso fil xiss fil = slf.wospph / "posso.py" if no fil.xiss(): sul["sus"] = HlhSus.OWN sul["o"] = " posso fil no foun" un sul # Bsi funionliy h wih opn(fil, '', noin='uf-8') s f: onn = f.() # h fo  possin pns pns = ['', 'sinl', 'fil', 'ff', 'nupy'] founpns = su(1 fo pn in pns if pn.low() in onn.low()) if founpns >= ln(pns) * 0.6: # 60% ov sul["sus"] = HlhSus.HLHY sul["is"] = [ Hlhi( n="pnsfoun", vlu=founpns, uni="pns", sus=HlhSus.HLHY, sipion=" possin pns " ) ] ls: sul["sus"] = HlhSus.WNIN sul["o"] = f"Lii  possin pbiliis: {ln(pns) - founpns} pns issin" xp xpion s : sul["sus"] = HlhSus.IIL sul["o"] = f" posso hlh h fil: {s()}" un sul syn f huonoousopiizhlh(slf) -> i[s, ny]: """h uonoous opiiz hlh""" sul = { "sus": HlhSus.UNNOWN, "is": [], "vsion": "", "upi": 0.0, "pnnis": ["nupy", "pns", "sii-ln"] } y: # h if opiiz fil xiss opiizfil = slf.wospph / "uonoousopiiz.py" if no opiizfil.xiss(): sul["sus"] = HlhSus.OWN sul["o"] = "uonoous opiiz fil no foun" un sul # Bsi suu h wih opn(opiizfil, '', noin='uf-8') s f: onn = f.() # h fo opiizion pns oppns = ['opiiz', 'uonoous', 'PI', 'bnh', 'synio'] founpns = su(1 fo pn in oppns if pn.low() in onn.low()) if founpns >= ln(oppns) * 0.7: # 70% ov sul["sus"] = HlhSus.HLHY sul["is"] = [ Hlhi( n="opiizpnsfoun", vlu=founpns, uni="pns", sus=HlhSus.HLHY, sipion="uonoous opiizion pns " ) ] ls: sul["sus"] = HlhSus.WNIN sul["o"] = f"Inopl opiizion fus: {ln(oppns) - founpns} pns issin" xp xpion s : sul["sus"] = HlhSus.IIL sul["o"] = f"uonoous opiiz hlh h fil: {s()}" un sul syn f honioinsyshlh(slf) -> i[s, ny]: """h onioin sys hlh""" sul = { "sus": HlhSus.HLHY, "is": [], "vsion": "1.0.0", "upi": i.i(), # Sys upi "pnnis": ["psuil", "synio"] } #  onioin-spifi is sul["is"] = [ Hlhi( n="onioinhspfo", vlu=ln(slf.hlhhisoy) if slf.hlhhisoy ls 0, uni="hs", sus=HlhSus.HLHY, sipion="Nub of hlh hs pfo" ), Hlhi( n="ivls", vlu=ln([ fo  in slf.ls if no .solv]), uni="ls", sus=HlhSus.HLHY, sipion="Nub of iv ls" ) ] un sul f lulovllsus(slf, oponns: Lis[oponnHlh]) -> HlhSus: """lul ovll sys hlh sus""" if no oponns: un HlhSus.UNNOWN # oun sus lvls susouns = {} fo oponn in oponns: sus = oponn.sus.vlu susouns[sus] = susouns.(sus, 0) + 1 # in ovll sus if susouns.(HlhSus.IIL.vlu, 0) > 0: un HlhSus.IIL lif susouns.(HlhSus.WNIN.vlu, 0) > 0: un HlhSus.WNIN lif susouns.(HlhSus.OWN.vlu, 0) > 0: un HlhSus.WNIN lif ll(.sus == HlhSus.HLHY fo  in oponns): un HlhSus.HLHY ls: un HlhSus.UNNOWN f nls(slf, po: Hlhpo) -> Lis[Sysl]: """n ls bs on hlh po""" ls = [] # h oponn sus hns if slf.lspo: fo oponn in po.oponns: pvoponn = Non fo pvop in slf.lspo.oponns: if pvop.oponnyp == oponn.oponnyp: pvoponn = pvop b if pvoponn n pvoponn.sus != oponn.sus: if oponn.sus in [HlhSus.IIL, HlhSus.OWN]: sviy = lSviy.IIL lif oponn.sus == HlhSus.WNIN: sviy = lSviy.WNIN ls: sviy = lSviy.INFO l = Sysl( li=f"l{in(i.i())}{oponn.oponnyp.vlu}", sviy=sviy, oponn=oponn.oponnn, ss=f"Sus hn fo {pvoponn.sus.vlu} o {oponn.sus.vlu}", ={ "pvioussus": pvoponn.sus.vlu, "unsus": oponn.sus.vlu, "sponsi": oponn.spo