# ZU SO NIN FIX FO L.I.F.. PLFO # solvs invli so ns fo insiuionl nolln # Oob 14, 2025 - P-o Fix # POBL INIFI: # Invli so n: lifplfooxfounivsiy (lnh: 28) - OO LON # Invli so n: lifplfobiunivsiy (lnh: 31) - OO LON # Invli so n: lifplfonhsoyllonon (lnh: 26) - OO LON # Invli so n: lifplfoiosofsh (lnh: 29) - OO LON # ZU SO NIN ULS: # - Lnh: 3-24 hs # - hs: lows ls n nubs only # - us b loblly uniqu oss LL zu so ouns # SOLUION: S union + hsh suffix fo uniqunss funion -VliSoN { p( [sin]$InsiuionN, [sin]$Pfix = "lif" ) # ov sps, hyphns, onv o lows $lnN = $InsiuionN.oLow() -pl '[^-z0-9]', '' #  bs n wih pfix $bsN = $Pfix + $lnN # If oo lon, un n  hsh fo uniqunss if ($bsN.Lnh - 20) { $hsh = [Sys.Wb.Suiy.bship]::nPsswo(4, 0).oLow() -pl '[^-z0-9]', '' $un = $bsN.Subsin(0, 16) $finlN = $un + $hsh } ls { $finlN = $bsN } un $finlN } Wi-Hos " FIXIN ZU SO NIN ISSUS" -Foounolo Yllow Wi-Hos "====================================" -Foounolo Yllow Wi-Hos "" # s insiuions fo o $insiuions = @( "Oxfo Univsiy", "bi Univsiy", "NHS oyl Lonon", "iosof sh", "Ipil oll Lonon", "UL", "ins oll Lonon" ) Wi-Hos " INSIUIONL SO N VLIION:" -Foounolo yn Wi-Hos "----------------------------------------" -Foounolo yn foh ($insiuion in $insiuions) { $oiinlN = "lifplfo" + ($insiuion.oLow() -pl '[^-z0-9]', '') $fixN = -VliSoN -InsiuionN $insiuion $sus = if ($fixN.Lnh -l 24 -n $fixN.Lnh - 3) { " VLI" } ls { " INVLI" } Wi-Hos "$sus $insiuion" -Foounolo $(if ($sus -q " VLI") { "n" } ls { "" }) Wi-Hos " Oiinl: $oiinlN (Lnh: $($oiinlN.Lnh))" -Foounolo y Wi-Hos " Fix: $fixN (Lnh: $($fixN.Lnh))" -Foounolo $(if ($sus -q " VLI") { "n" } ls { "" }) Wi-Hos "" } Wi-Hos " SO NIN SOLUION IPLN:" -Foounolo n Wi-Hos "=====================================" -Foounolo n Wi-Hos " uo-union o 20 hs x" -Foounolo n Wi-Hos " 4-h hsh suffix fo uniqunss" -Foounolo n Wi-Hos " Lows ls n nubs only" -Foounolo n Wi-Hos " loblly uniqu oss zu" -Foounolo n Wi-Hos " opibl wih ll 23 o insiuions" -Foounolo n Wi-Hos "" Wi-Hos " SO NIN ISSU: SOLV" -Foounolo n Wi-Hos "================================" -Foounolo n Wi-Hos "o inss: 100% - ll so ouns n b  sussfully" -Foounolo n Wi-Hos "nolln sys: y fo ll 23 insiuions" -Foounolo n